import { Router } from 'express';
import { createUser, getUser, updateUser, deleteUser, } from '../controllers/userController.js';
import { asyncHandler } from '../middlewares/asyncHandler.js';
import { UserValidators, validate } from '../middlewares/validator.js';
import { isLoggedIn } from '../middlewares/isLoggedIn.js';
import { UserRepository } from '../repositories/userRepository.js';
import { prisma } from '../lib/constants.js';
const userRouter = Router();
const userRepository = new UserRepository(prisma);
const { registerValidator, deleteValidator } = UserValidators(userRepository);
userRouter.post('/', registerValidator, validate, asyncHandler(createUser));
userRouter.get('/', isLoggedIn, asyncHandler(getUser));
userRouter.patch('/', isLoggedIn, asyncHandler(updateUser));
userRouter.delete('/', isLoggedIn, deleteValidator, validate, asyncHandler(deleteUser));
export { userRouter };
